import React, { useEffect, useState } from 'react'
import axios from 'axios'


function Results() {

    const [students, setStudents] = useState([])

    const [selectedStudent, setSelectedStudent] = useState('')
    const [result, seResult] = useState({})

    useEffect(() => {
        fetchStudents()
    }, [])

    const fetchStudents = async () => {
        const response = await axios.get('http://localhost:4000/students')
        setStudents(response.data)
    }

    const handleStudentChange = (event) => {
        setSelectedStudent(event.target.value)
    }

    useEffect(() => {
        fetchResult()
    }, [selectedStudent])

    const fetchResult = async () => {
        if (!selectedStudent) return
        const response = await axios.get('http://localhost:4000/results')
        const result = response.data.find(response => {
            return response.Student === selectedStudent
        })
        const formattedResult = await axios.get('http://localhost:4000/results/formatted/' + result._id)
        seResult(formattedResult)
    }

    return (
        <div className="mt-12 text-5xl text-center">
            <h1>Results</h1>

            <div className="flex text-2xl flex-col w-11/12 mx-auto sm:w-9/12 md:w-8/12 mt-12">
                <div className="overflow-x-auto sm:-mx-6 lg:-mx-8">
                    <div className="inline-block min-w-full py-2 sm:px-6 lg:px-8">
                        <div className="overflow-hidden">
                            <label htmlFor="students">Choose Result of student:</label><br />
                            <select className='mt-4 text-xl' name="students" id="students" onChange={handleStudentChange} value={selectedStudent}>
                                <option value="" disabled selected>Select an option</option>
                                {students && students.map(student => (
                                    <option value={student._id}>{student.firstName} {student.lastName}</option>
                                ))}
                            </select>
                        </div>
                    </div>
                </div>
            </div>


            {selectedStudent && result && (
                <div className="flex flex-col w-11/12 mx-auto sm:w-9/12 md:w-8/12 mt-10">
                    <div className="overflow-x-auto sm:-mx-6 lg:-mx-8">
                        <div className="inline-block min-w-full py-2 sm:px-6 lg:px-8">
                            <div className="overflow-hidden">
                                <table className="min-w-full text-sm font-light text-center sm:text-lg">
                                    <thead className="font-medium border-b">
                                        <tr>
                                            <th scope="col" className="px-6 py-4">#id</th>
                                            <th scope="col" className="px-6 py-4">Signed By</th>
                                            <th scope="col" className="px-6 py-4">Student Info</th>
                                            <th scope="col" className="px-6 py-4">Marks</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        <tr
                                            key={index}
                                            className="transition duration-300 ease-in-out border-b hover:bg-gray-200">
                                            <td className="px-6 py-4 font-medium whitespace-nowrap">{index + 1}</td>
                                            <td className="px-6 py-4 whitespace-nowrap">{data.Signed_By}</td>
                                            <td className="px-6 py-4 whitespace-nowrap">{data[1]}</td>
                                        </tr>
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </div>
            )}

        </div>
    )
}

export default Results
