import React, { useEffect, useState } from 'react'
import axios from 'axios'

function ResultForm() {
    const [students, setStudents] = useState([])
    const [selectedStudent, setSelectedStudent] = useState('')


    const fetchStudents = async () => {
        const response = await axios.get('http://localhost:4000/students')
        setStudents(response.data)
    }
    useEffect(() => {
        fetchStudents()
    }, [])



    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        password: '',
        email: '',
    })

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value })
    }

    const handleSubmit = async (e) => {
        e.preventDefault()
        try {
            const response = await axios.post('http://localhost:4000/students', formData)
            console.log('Data posted:', response.data)
            alert("Student Created")
            setFormData({
                firstName: '',
                lastName: '',
                password: '',
                email: '',
            })
        } catch (error) {
            console.error('Error:', error)
        }
    }

    return (
        <div className="w-full max-w-md  mx-auto">
            <form onSubmit={handleSubmit} className="bg-white mt-32 shadow-md rounded px-8 pt-6 pb-8 mb-4">
                <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="firstName">
                        Student
                    </label>
                    <select className='mt-4 text-xl' name="students" id="students" onChange={handleStudentChange} value={selectedStudent}>
                        <option value="" disabled selected>Select an option</option>
                        {students && students.map(student => (
                            <option value={student._id}>{student.firstName} {student.lastName}</option>
                        ))}
                    </select>
                    {/* <input
                        className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                        id="firstName"
                        type="text"
                        placeholder="First Name"
                        name="firstName"
                        value={formData.firstName}
                        onChange={handleChange}
                    /> */}
                </div>
                <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="lastName">
                        Last Name
                    </label>
                    <input
                        className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                        id="lastName"
                        type="text"
                        placeholder="Last Name"
                        name="lastName"
                        value={formData.lastName}
                        onChange={handleChange}
                    />
                </div>
                <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="password">
                        Password
                    </label>
                    <input
                        className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                        id="password"
                        type="password"
                        placeholder="Password"
                        name="password"
                        value={formData.password}
                        onChange={handleChange}
                    />
                </div>
                <div className="mb-6">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="email">
                        Email
                    </label>
                    <input
                        className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                        id="email"
                        type="email"
                        placeholder="Email"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                    />
                </div>
                <div className="flex items-center justify-between">
                    <button
                        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                        type="submit"
                    >
                        Submit
                    </button>
                </div>
            </form>
        </div>
    )
}

export default ResultForm
